base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:77:"http://blog.mailchimp.com/pro-features-and-quick-actions-on-mailchimp-mobile/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:86:"http://blog.mailchimp.com/pro-features-and-quick-actions-on-mailchimp-mobile/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 10 Nov 2015 15:21:41 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:4:{i:0;a:5:{s:4:"data";s:14:"MailChimp News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:16:"mailchimp mobile";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:13:"MailChimp Pro";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:7:"release";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://blog.mailchimp.com/?p=91361";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:354:"Back in September, we introduced MailChimp Pro, our powerful new feature add-on for quickly growing MailChimp users with lists of 50,000 or more. Since then, we&#8217;ve explored some of the new features, and even showed you how we used MailChimp Pro to market MailChimp Pro. And today, we&#8217;re happy to announce that Pro functionality is making ...";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Drew";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:3122:"<p>Back in September, <a href="http://blog.mailchimp.com/introducing-mailchimp-pro/" target="_blank">we introduced MailChimp Pro</a>, our powerful new feature add-on for quickly growing MailChimp users with lists of 50,000 or more. Since then, we&#8217;ve explored <a href="http://blog.mailchimp.com/multivariate-testing-for-mailchimp-pro/" target="_blank">some</a> of <a href="http://blog.mailchimp.com/comparative-reports-for-mailchimp-pro/" target="_blank">the</a> new <a href="http://blog.mailchimp.com/compliance-insights-for-mailchimp-pro/" target="_blank">features</a>, and even showed you how <a href="http://blog.mailchimp.com/mailchimp-pro-for-mailchimp-marketing/" target="_blank">we used MailChimp Pro to market MailChimp Pro</a>. And today, we&#8217;re happy to announce that Pro functionality is making its way to our mobile app.</p>\n<p>With MailChimp Mobile 3.6, Pro users can now track a campaign’s delivery or view the results of a multivariate test. We&#8217;ve also added a couple Quick Actions for iPhone 6S and 6S Plus users. Here&#8217;s a look at what&#8217;s new.</p>\n<p>&nbsp;</p>\n<figure class="img column-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/11/Delivery_Insight-672x244.png" alt="Delivery_Insight" /></figure>\n<h2>Delivery Insights</h2>\n<p>Much like it does in the MailChimp web app, Delivery Insights now appears at the top of a campaign&#8217;s report page on mobile devices. Pro users can see the current progress of a sending campaign in real time. And iOS users can cancel a campaign mid-send with Stop Delivery.</p>\n<p>&nbsp;</p>\n<figure class="img column-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/11/MVT_Reports-672x419.png" alt="MVT_Reports" /></figure>\n<h2>Multivariate Testing reports</h2>\n<p>After sending a multivariate campaign, Pro users can now view the report on their mobile device in the <strong>Reports</strong> tab. Learn which combination performed best, if a winner was automatically selected, and even drill down to the specifics of each campaign—all on the go, wherever you are.</p>\n<p>&nbsp;</p>\n<figure class="img column-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/11/IMG_3661-672x520.jpg" alt="IMG_3661" /></figure>\n<h2>Quick Actions</h2>\n<p>With the introduction of 3D touch on the iPhone 6S and 6S Plus, Apple gave us some new ways to interact with our apps. <a href="http://www.apple.com/iphone-6s/3d-touch/" target="_blank">Quick Actions</a> let you jump directly into an app to perform a specific action in fewer steps than usual. You can access these actions by pressing on the app’s icon on your home screen. In MailChimp Mobile 3.6, we’ve added two Quick Actions: <strong>Add Subscriber</strong> and <strong>View Latest Report</strong>. They do exactly what you’d expect, just faster than ever before.</p>\n<h2>What&#8217;s next for MailChimp Mobile?</h2>\n<p>Good question. We&#8217;ve got some big updates coming that we&#8217;re not <em>quite</em> ready to talk about. But know that we&#8217;ll be tweaking a lot and looking forward to your feedback.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:29:"http://search.yahoo.com/mrss/";a:1:{s:7:"content";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:73:"http://blog.mailchimp.com/wp-content/uploads/2015/11/IMG_3661-600x464.jpg";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:82:"http://blog.mailchimp.com/pro-features-and-quick-actions-on-mailchimp-mobile/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"0";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:1;a:6:{s:4:"data";s:86:"\n    \n    \n    \n    \n    \n    		\n		\n		\n		\n		\n		\n\n    \n    \n        \n        \n    \n    ";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:6:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:36:"Featured Integrations: November 2015";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:62:"http://blog.mailchimp.com/featured-integrations-november-2015/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:71:"http://blog.mailchimp.com/featured-integrations-november-2015/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 03 Nov 2015 18:43:10 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:6:{i:0;a:5:{s:4:"data";s:14:"MailChimp News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:10:"40 Nuggets";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:21:"featured integrations";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:10:"Magemonkey";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:4;a:5:{s:4:"data";s:10:"MotionMail";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:5;a:5:{s:4:"data";s:7:"Vextras";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://blog.mailchimp.com/?p=91165";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:398:"It&#8217;s November, and we&#8217;re scurrying around making sure everything is ready for the upcoming holiday rush. In the spirit of the impending season, I want to show off some tools that&#8217;ll help you get the most out of your email marketing efforts over the next few weeks. Whether you&#8217;re looking to do some automation or aiming to grow your list, these integrations will ...";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Nate";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:4761:"<p>It&#8217;s November, and we&#8217;re scurrying around making sure everything is ready for the <a href="http://mailchimp.com/holiday-tips/" target="_blank">upcoming holiday rush</a>. In the spirit of the impending season, I want to show off some tools that&#8217;ll help you get the most out of your email marketing efforts over the next few weeks. Whether you&#8217;re looking to do some automation or aiming to grow your list, these integrations will help you have the best holidays ever.</p>\n<h2>Vextras Messaging Workflows</h2>\n<figure class="img column-width ghost-browser"><a href="https://connect.mailchimp.com/integrations/boomerang-for-bigcommerce?_ga=1.30787199.1722026757.1415390374" target="_blank"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/11/vextras-672x569.png" alt="vextras" /></a></figure>\n<p>Online selling suite Vextras has a history of building some really solid integrations for MailChimp and Mandrill. You may remember their previous apps, Postmaster and Boomerang. Now they&#8217;ve totally re-engineered those apps from scratch and reorganized them into a single platform. The new integration connects with Volusion, Bigcommerce, and 3D Cart right out of the box, with more platforms to come. Vextras has beefed up a lot of the follow-up email options with complex logic, so now you can send specific abandoned cart emails, cross-promote your other products based on customer purchases, and even track customer loyalty based on order history.</p>\n<p><a href="https://connect.mailchimp.com/integrations/boomerang-for-bigcommerce" target="_blank">Connect to Vextras Messaging Workflows for BigCommerce</a></p>\n<h2>MotionMail</h2>\n<figure class="img column-width ghost-browser"><a href="https://connect.mailchimp.com/integrations/motionmail-countdown-timers" target="_blank"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/11/motionmail-672x330.png" alt="motionmail" /></a></figure>\n<p>If you&#8217;re a retailer, you&#8217;re probably counting down the days to Black Friday and Cyber Monday. If you want to take your customers along for the ride and build up some suspense before the big day(s), MotionMail makes it easy to embed a dynamic countdown timer right in your email. They offer free and paid plans, and you can pick from a number of predesigned fonts and customize the color scheme to make it fit with your brand&#8217;s style. Hurry up, the clock is ticking.</p>\n<p><a href="https://connect.mailchimp.com/integrations/motionmail-countdown-timers" target="_blank">Connect to MotionMail</a></p>\n<h2>Magemonkey</h2>\n<figure class="img column-width ghost-browser"><a href="https://connect.mailchimp.com/integrations/magento" target="_blank"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/10/magemonkey-672x630.png" alt="magemonkey" /></a></figure>\n<p>Magento is among the most popular e-commerce platforms for MailChimp users. Its flexibility makes it a no-brainer for anyone who likes to tinker and customize. And you can do even more with Magemonkey, our Magento extension, made by the folks at ebizmarts. It lets you sync over purchase data as subscribers check out through your Magento store, which gives you extra ways to segment your list in MailChimp or kick off an automation workflow as a follow-up. If you&#8217;re a Mandrill user, there&#8217;s an added abandoned cart feature that lets you track when someone doesn&#8217;t complete the checkout process.</p>\n<p><a href="https://connect.mailchimp.com/integrations/magento" target="_blank">Connect to Magemonkey</a></p>\n<h2>40 Nuggets</h2>\n<figure class="img column-width ghost-browser"><a href="https://connect.mailchimp.com/integrations/40nuggets-for-mailchimp" target="_blank"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/11/40nuggets-672x378.png" alt="40nuggets" /></a></figure>\n<p>A few months ago, we showed off <a href="https://blog.mailchimp.com/how-we-made-a-non-annoying-popup-form-for-the-thread/?_ga=1.68018897.1722026757.1415390374" target="_blank">how you can make a non-annoying popup form</a> for your website. With 40 Nuggets, you can make those non-annoying forms pretty darn smart, too. Set up triggered behavioral rules for displaying your signup form (or other content) to help get the right people at the right time. One of their coolest features is a custom form builder which lets you upload an image of the form you want to use and then map fields over the top of it. <a href="http://38.media.tumblr.com/b404cab7fda3f66de6ede58bc0db7fe0/tumblr_mqxb30f98C1qgoqf9o1_500.gif" target="_blank">Time to get poppin&#8217;</a>.</p>\n<p><a href="https://connect.mailchimp.com/integrations/40nuggets-for-mailchimp" target="_blank">Connect to 40 Nuggets</a></p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:29:"http://search.yahoo.com/mrss/";a:1:{s:7:"content";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:72:"http://blog.mailchimp.com/wp-content/uploads/2015/11/vextras-600x508.png";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:67:"http://blog.mailchimp.com/featured-integrations-november-2015/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:2;a:6:{s:4:"data";s:74:"\n    \n    \n    \n    \n    \n    		\n		\n\n    \n    \n        \n        \n    \n    ";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:5:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:49:"How to Send Emails That Gmail Users Won’t Block";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://blog.mailchimp.com/how-to-send-emails-that-gmail-users-wont-block/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:82:"http://blog.mailchimp.com/how-to-send-emails-that-gmail-users-wont-block/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Thu, 22 Oct 2015 13:49:04 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:2:{i:0;a:5:{s:4:"data";s:14:"deliverability";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:5:"gmail";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://blog.mailchimp.com/?p=91061";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:329:"In September, Gmail added a “block sender” feature to their web interface and mobile app. Now, when you get an email from a sender you’re not comfortable with, you&#8217;ve got options. Instead of marking the email as spam, unsubscribing, or manually setting up a filter to remove it from your inbox, you can block that ...";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Anna";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:3626:"<p>In September, Gmail added a <a href="https://support.google.com/mail/answer/8151?hl=en" target="_blank">“block sender” feature</a> to their web interface and mobile app. Now, when you get an email from a sender you’re not comfortable with, you&#8217;ve got options. Instead of marking the email as spam, unsubscribing, or manually setting up a filter to remove it from your inbox, you can block that sender from your inbox with one click.</p>\n<p>This is great news if you love to keep your own inbox tidy. But what does it mean for you as an email marketer? I’m here from MailChimp’s deliverability team to help you out.</p>\n<p>When you click “block sender” in Gmail, a filter is created that sends all future emails from the sender’s address directly to your spam folder. Just like with other filtered messages, the sender is not directly notified. The tab heading in Gmail&#8217;s settings menu has changed from just "Filters" to "Filters and Blocked Addresses,” which associates these 2 actions together.</p>\n<p>But just because “block sender” is separate from Gmail’s “report spam” feature doesn’t mean it affects a sender’s reputation any less. In fact, since clicking “block sender” means that a sender’s messages will now automatically go to spam, we can expect that a sender’s Gmail reputation will get worse the more they’re blocked by recipients. Marking something as spam in your inbox feeds into Gmail’s global spam-filtering algorithms for other users, and Gmail weighs user-reported spam heaviest when filtering spam.</p>\n<p>So, how can you prevent your subscribers from clicking “block sender” on your campaigns? Start by thinking about why someone would use the block feature in the first place. Maybe they don’t trust a sender’s opt-out process, or maybe they don’t trust the sender anymore because the content isn’t what they expected. Maybe they’re overwhelmed by the number of emails they receive from a single sender. Maybe they don’t remember who the sender is or where they signed up for the emails in the first place.</p>\n<p>You can avoid causing subscribers this kind of confusion by following some basic email marketing best practices. In other words, check yourself before you wreck yourself:</p>\n<ul>\n<li>Before every send, ask yourself if your subscribers know who you are and if they expect this specific kind of content from you.</li>\n<li>Pay attention to your unsubscribes to make sure your subscribers aren’t accidentally receiving multiple emails from you.</li>\n<li>Frequently <a href="http://kb.mailchimp.com/lists/managing-subscribers/remove-inactive-subscribers" target="_blank">prune unengaged subscribers</a> to avoid sending to people who have blocked (or might start blocking) your campaigns.</li>\n<li>Include an <a href="http://kb.mailchimp.com/campaigns/design/add-an-update-profile-link" target="_blank">“update profile” option</a> in your campaigns so subscribers can select how often they’d like to hear from you</li>\n</ul>\n<p>This new “block sender” feature might seem like a bad thing for email marketers, but it doesn’t have to be. In fact, if it gets you to be more deliberate in putting together your campaigns and managing your lists, it may even lead to better emails and better subscriber engagement overall. And with the <a href="http://mailchimp.com/holiday-tips/" target="_blank">holidays</a> quickly approaching, now’s the perfect time to reevaluate what you’re sending and check your list (<a href="http://media.giphy.com/media/O9eAXberbL88o/giphy.gif" target="_blank">twice</a>).</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:78:"http://blog.mailchimp.com/how-to-send-emails-that-gmail-users-wont-block/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"2";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:3;a:6:{s:4:"data";s:80:"\n    \n    \n    \n    \n    \n    		\n		\n		\n		\n\n    \n    \n        \n        \n    \n    ";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:6:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:43:"Using MailChimp Pro to Market MailChimp Pro";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:64:"http://blog.mailchimp.com/mailchimp-pro-for-mailchimp-marketing/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:73:"http://blog.mailchimp.com/mailchimp-pro-for-mailchimp-marketing/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 20 Oct 2015 13:38:49 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:4:{i:0;a:5:{s:4:"data";s:14:"MailChimp News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:19:"comparative reports";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:13:"MailChimp Pro";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:20:"multivariate testing";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://blog.mailchimp.com/?p=90677";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:333:"Before we released MailChimp Pro, we tested it. Then we tested it some more. After a year of creating and testing, we were proud to have built something that benefits not only our users, but also the email marketing we share with them. When we started thinking about how to announce the release of MailChimp Pro to the world, ...";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Brad";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:5109:"<p>Before we released <a href="http://mailchimp.com/pro/" target="_blank">MailChimp Pro</a>, we <a href="http://blog.mailchimp.com/multivariate-testing-for-mailchimp-pro/" target="_blank">tested it</a>. Then we <a href="http://us5.campaign-archive2.com/?u=7e093c5cf4&amp;id=67d2df4708" target="_blank">tested it some more</a>. After a year of creating and testing, we were proud to have built something that benefits not only our users, but also the <a href="http://mailchimp.com/about/email-archives/" target="_blank">email marketing we share with them</a>. When we started thinking about how to announce the release of MailChimp Pro to the world, we knew we were working with the best tools for the job. And, predictably, we tested even more. Here&#8217;s a little look at all the experimentation that went into the creation and early marketing of Pro.</p>\n<h2>Testing Multivariate Testing</h2>\n<p>We got warmed up for using Pro to announce Pro by using it to roll out a few more product announcements. Since the MailChimp marketing team is just a 30-second <a href="https://en.wikipedia.org/wiki/Heelys" target="_blank">Heely</a> ride away from the product team, they let us try all the new features while they were still being developed. So before Pro was out in the world we started working on 8 versions of an email about our new A/B testing features. We followed that up by testing 5 variations of an email about <a href="http://mailchimp.com/features/mailchimp-subscribe/" target="_blank">MailChimp Subscribe</a>.</p>\n<p>&nbsp;</p>\n<figure class="img full-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/10/mv-tests.png" alt="Multivariate tests" /></figure>\n<p>When it came time to work on our big Pro emails, we were ready. With 2 multivariate campaigns under our belt, we could use what we learned to set up set up a campaign we knew would be a high-performer. There&#8217;s a prominent image, immediate call to action, minimal text, and—thanks to our email developer <a href="https://twitter.com/alexkellysays" target="_blank">Alex Kelly</a>—a universal design, consistent everywhere from Outlook 2000 to that Android phone where texts scroll across the bezel.</p>\n<p>Here&#8217;s how the draft of the email changed as we tweaked it:</p>\n<p>&nbsp;</p>\n<figure class="img full-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/10/pro-four.png" alt="Pro announcements over time" /></figure>\n<h2>Comparing Comparative Reports</h2>\n<p>Our standard reporting features have always been instructive in developing our own email marketing plans, and I knew working with <a href="https://blog.mailchimp.com/comparative-reports-for-mailchimp-pro/" target="_blank">Pro&#8217;s new Comparative Reports feature</a> would be even more illuminating. When we first planned Pro&#8217;s email marketing, we created 4 sets of messaging targeting 4 types of users. A few revisions later, we settled on a single message for everyone—but we still wanted to break out performance for each group. Comparative Reports made that easy.</p>\n<figure class="img column-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/10/segment-1.jpg" alt="segment-1" /></figure>\n<p>Sure, we&#8217;ve exported a few lists in our day and are no strangers to Microsoft Excel. But once we generated our first comparative report, I knew there was no turning back. We could do more at once, do it faster, and learn new things that we never expected. Right away, 4 tasks became 1. So why not use all the time we saved to run a few more tests? (Y&#8217;all, we<em> love </em>tests.)</p>\n<p>We looked at the campaign performance for free users versus paid users, plus the people who installed Pro and some customers we&#8217;ve <a href="http://kb.mailchimp.com/lists/managing-subscribers/designate-and-send-to-vip-subscribers" target="_blank">marked as VIPs</a>.</p>\n<figure class="img column-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/10/segment-2.jpg" alt="segment-2" /></figure>\n<p>In just a second, we can see how these groups compared to each other, but we&#8217;re also able to figure out that 70% of Pro subscribers opened the email and about half of those clicked through to the landing page.</p>\n<p>And with additional data passed into our list, we&#8217;re able to use Comparative Reports to isolate results for any segment imaginable. Plus, when we re-send our announcement to new susbcribers, we&#8217;re able to combine stats from both campaigns with 2 clicks.</p>\n<figure class="img column-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/10/segment-3.jpg" alt="segment-3" /></figure>\n<h2>I &lt;3 MailChimp Pro</h2>\n<p>This may sound like tooting our own horn, but really, MailChimp Pro has changed the way I do email marketing for MailChimp. From here on out, every one of our campaigns will use Multivariate Testing, Comparative Reports, or a combination of both. It&#8217;s changed how we send, what we send, and how quickly we can iterate. And from what I hear, this is only the beginning. Stay tuned.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:29:"http://search.yahoo.com/mrss/";a:1:{s:7:"content";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:74:"http://blog.mailchimp.com/wp-content/uploads/2015/10/segment-3-600x223.jpg";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:69:"http://blog.mailchimp.com/mailchimp-pro-for-mailchimp-marketing/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"2";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:4;a:6:{s:4:"data";s:92:"\n    \n    \n    \n    \n    \n    		\n		\n		\n		\n		\n		\n		\n		\n\n    \n    \n        \n        \n    \n    ";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:6:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:92:"MailChimp Partners with Facebook, Twitter, Google, Shopify, and WooCommerce for the Holidays";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:115:"http://blog.mailchimp.com/mailchimp-partners-with-facebook-twitter-google-shopify-and-woocommerce-for-the-holidays/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:124:"http://blog.mailchimp.com/mailchimp-partners-with-facebook-twitter-google-shopify-and-woocommerce-for-the-holidays/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 07 Oct 2015 14:00:25 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:8:{i:0;a:5:{s:4:"data";s:14:"MailChimp News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:10:"automation";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:8:"Facebook";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:6:"google";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:4;a:5:{s:4:"data";s:7:"holiday";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:5;a:5:{s:4:"data";s:7:"shopify";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:6;a:5:{s:4:"data";s:7:"Twitter";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:7;a:5:{s:4:"data";s:11:"WooCommerce";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://blog.mailchimp.com/?p=90437";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:315:"For a lot of small businesses, e-commerce sellers, and marketers, the holidays are a pretty critical time. How well a company performs during the holiday season can have a huge impact on its success in the coming year. So what can you do to help ensure that success? We’re maybe a little biased, but we think ...";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:7:"Allyson";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:1865:"<figure class="img full-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/10/blog.jpg" alt="blog" /></figure>\n<p>For a lot of small businesses, e-commerce sellers, and marketers, the holidays are a pretty critical time. How well a company performs during the holiday season can have a huge impact on its success in the coming year. So what can you do to help ensure that success?</p>\n<p>We’re maybe a little biased, but we think email marketing is one of the best ways to drive audience engagement and revenue, but especially at year&#8217;s end. To help MailChimp users have a great holiday season, we&#8217;ve compiled some great resources to help you do your best during the most wonderful time of the year.</p>\n<p>First, we’ve partnered with Facebook, Twitter, Google, Shopify, and WooCommerce to create an educational automated email series full of great advice and best practices. Subscribe and you&#8217;ll learn how to avoid spam filters, how to set up effective Twitter Ad campaigns, how to make holiday campaigns that speak to your subscribers, how to review your Facebook Ad performance, and more. Each installment will be delivered right to your inbox, completely free, one per day, until the series ends. (Pro-tip: Stick around until the end for a special holiday surprise.)</p>\n<p>We’ve also put together a website with 100 quick, practical tips you can execute throughout the next few months. And for further inspiration we’ve included real-life campaigns from dozens of MailChimp users to show you the full range of what&#8217;s possible.</p>\n<p><a href="http://mailchimp.com/holiday-tips/" target="_blank">Visit the site</a> to sign up for the email series and explore all the tips. It&#8217;s too early to wish you "happy holidays" just yet, but it&#8217;s always the right time for some happy emailing.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:29:"http://search.yahoo.com/mrss/";a:1:{s:7:"content";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:69:"http://blog.mailchimp.com/wp-content/uploads/2015/10/blog-600x335.jpg";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:120:"http://blog.mailchimp.com/mailchimp-partners-with-facebook-twitter-google-shopify-and-woocommerce-for-the-holidays/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"0";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:5;a:6:{s:4:"data";s:89:"\n    \n    \n    \n    \n    \n    		\n		\n		\n		\n		\n		\n		\n\n    \n    \n        \n        \n    \n    ";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:6:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:35:"Featured Integrations: October 2015";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:61:"http://blog.mailchimp.com/featured-integrations-october-2015/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:70:"http://blog.mailchimp.com/featured-integrations-october-2015/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 06 Oct 2015 15:23:39 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:7:{i:0;a:5:{s:4:"data";s:14:"MailChimp News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:21:"featured integrations";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:15:"Fresh Relevance";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:9:"Instapage";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:4;a:5:{s:4:"data";s:7:"Twitter";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:5;a:5:{s:4:"data";s:26:"Twitter Tailored Audiences";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:6;a:5:{s:4:"data";s:11:"WooCommerce";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://blog.mailchimp.com/?p=90277";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:352:"The holidays are approaching quickly, and if you’re in the e-commerce or retail industry, that means your life is about to be super busy. (And let&#8217;s be real: it probably is already.) To help you survive prepare, through the end of the year, we&#8217;ll be featuring integrations that will help online sellers like you to grow your lists, ...";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Nate";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:4877:"<p>The holidays are approaching quickly, and if you’re in the e-commerce or retail industry, that means your life is about to be super busy. (And let&#8217;s be real: it probably is already.) To help you <span style="text-decoration: line-through;">survive</span> prepare, through the end of the year, we&#8217;ll be featuring integrations that will help online sellers like you to grow your lists, sell more, and better target your ideal customers. This month, we’re easing in with a look at integrations for businesses of all stages and sizes.</p>\n<h2>Instapage</h2>\n<figure class="img column-width ghost-browser"><a href="https://connect.mailchimp.com/integrations/instapage-landing-pages" target="_blank"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/09/instapage-672x409.png" alt="instapage" /></a></figure>\n<p>If you&#8217;re a small business, you may not have the resources to build a big, professional-grade landing page for your site. But you <em>do</em> have Instapage, which offers a variety of top-notch landing page templates that connect with MailChimp and help you easily collect new subscribers. All you need is a MailChimp API key and a free Instapage account to get started. If you&#8217;re looking for something a little more advanced, you can even use the Instapage integration in combination with <a href="http://mailchimp.com/features/automation/" target="_blank">our automation workflows</a> to kick off a welcome campaign for your new customers.</p>\n<p><a href="https://connect.mailchimp.com/integrations/instapage-landing-pages" target="_blank">Connect to Instapage</a></p>\n<h2>Twitter Tailored Audiences</h2>\n<figure class="img column-width ghost-browser"><a href="https://connect.mailchimp.com/integrations/twitter-tailored-audiences" target="_blank"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/09/twitter-672x211.png" alt="twitter" /></a></figure>\n<p>If you have a Twitter Ads account and are connected with MailChimp&#8217;s built-in Twitter integration, Twitter Tailored Audiences is a great way to reach a specific segment of your audience through a new medium. Let&#8217;s say you&#8217;re a business trying to build up interest in your online holiday sales. With MailChimp&#8217;s segmenting options, you can filter your subscribers based on who your best customers are, then send that segment to Twitter. Now, when you set your promoted tweet budget in Twitter&#8217;s Ads dashboard, you can be sure that they&#8217;re going to the right people, and driving more business back to your site while <a href="http://blog.mailchimp.com/reducing-irrelevance/" target="_blank">reducing irrelevance</a>.</p>\n<p><a href="https://connect.mailchimp.com/integrations/twitter-tailored-audiences" target="_blank">Connect to Twitter Tailored Audiences</a></p>\n<h2>Fresh Relevance</h2>\n<figure class="img column-width ghost-browser"><a href="https://connect.mailchimp.com/integrations/fresh-relevance" target="_blank"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/10/fr-672x386.jpg" alt="fr" /></a></figure>\n<p>Fresh Relevance&#8217;s marketing automation features allow you to send product recommendations, abandoned shopping cart emails, and more. Their suite of tools is great for medium-sized, e-commerce businesses on a variety of platforms. Oh, and you can build complex, hierarchical business rules for displaying conditional content and see at how well your emails are doing with their built-in analytics dashboard. Bonus: they also integrate with Mandrill.</p>\n<p><a href="https://connect.mailchimp.com/integrations/fresh-relevance" target="_blank">Connect to Fresh Relevance</a></p>\n<h2>WooCommerce Newsletter</h2>\n<figure class="img column-width ghost-browser"><a href="https://connect.mailchimp.com/integrations/woocommerce-newsletter-subscription" target="_blank"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/09/woo-672x390.png" alt="woo" /></a></figure>\n<p>If you&#8217;re an online seller who uses WordPress, you&#8217;re probably familiar with WooCommerce. Now, with WooCommerce&#8217;s Newsletter integration, you can automatically sync new purchasers over to your MailChimp list. WooCommerce uses our eCommerce360 API to sync over purchase data, too. With that info, you can kick off automation workflows or segment your list to find your subscribers who have purchased specific items. With the changes we made to automation workflows last month, you can even do both at the same time. For instance, you could kick off a "related products" automation workflow to complement the initial purchase. If a subscriber makes a follow-up purchase, you can segment them out so they stop getting those follow-up emails. Woo, indeed!</p>\n<p><a href="https://connect.mailchimp.com/integrations/woocommerce-newsletter-subscription" target="_blank">Connect to WooCommerce</a></p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:29:"http://search.yahoo.com/mrss/";a:1:{s:7:"content";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:67:"http://blog.mailchimp.com/wp-content/uploads/2015/10/fr-600x345.jpg";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:66:"http://blog.mailchimp.com/featured-integrations-october-2015/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"0";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:6;a:6:{s:4:"data";s:74:"\n    \n    \n    \n    \n    \n    		\n		\n\n    \n    \n        \n        \n    \n    ";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:6:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:79:"MailChimp and Skillshare Empower Small Businesses with $100,000 in Scholarships";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:104:"http://blog.mailchimp.com/mailchimp-and-skillshare-empower-small-businesses-with-100000-in-scholarships/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:113:"http://blog.mailchimp.com/mailchimp-and-skillshare-empower-small-businesses-with-100000-in-scholarships/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Sep 2015 14:28:10 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:2:{i:0;a:5:{s:4:"data";s:14:"MailChimp News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:10:"skillshare";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://blog.mailchimp.com/?p=90237";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:384:"When we launched our first Skillshare class earlier this year, I had to manage my expectations. I tend to get pretty excited about email marketing, but most people don&#8217;t usually share my enthusiasm. So I would’ve been happy if a few hundred folks found the course helpful. But here we are, more than 15,000 students later! I&#8217;m thrilled to have my expectations ...";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:7:"Allyson";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:5641:"<figure class="img column-width">\n<figure class="img column-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/09/skill_share_ads_facebookcard_1200x628_retina-672x352.jpg" alt="skill_share_ads_facebookcard_1200x628_retina" /></figure>\n</figure>\n<p><span style="font-weight: 400;">When we </span><span style="font-weight: 400;"><a href="https://twitter.com/MailChimp/status/585470097562673152" target="_blank">launched our first Skillshare class</a> earlier this year</span><span style="font-weight: 400;">, I had to manage my expectations. I tend to get pretty excited about email marketing, but most people don&#8217;t usually share my enthusiasm. So I would’ve been happy if a few hundred folks found the course helpful. But here we are, more than 15,000 students later! I&#8217;m thrilled to have my expectations so thoroughly surpassed.</span></p>\n<p><span style="font-weight: 400;">Maybe I shouldn&#8217;t have been so cautious. After all, MailChimp and Skillshare have a ton in common—namely, our dedicated audiences of e-commerce sellers, entrepreneurs, small business owners, marketers, and more, all invested in learning about new tools and techniques that help their businesses grow. </span></p>\n<p><span style="font-weight: 400;">That&#8217;s why we’re proud to be partnering with Skillshare as they announce a new focus on empowering small businesses. Together, we‘ll be awarding $100,000 in annual scholarships granting small business owners access to Skillshare’s full educational catalog. This includes our original Skillshare course (<a href="http://skl.sh/1MDEsQB" target="_blank">Getting Started with Email Marketing</a>), plus 3 new MailChimp courses, <em>plus</em> other courses like </span><a href="https://www.skillshare.com/classes/business/The-Modern-Marketing-Workshop/96411401?via=browse-popular-business-layout-grid" target="_blank"><span style="font-weight: 400;">The Modern Marketing Workshop</span></a><span style="font-weight: 400;"> with Seth Godin, </span><a href="https://www.skillshare.com/classes/business/Context-is-Key-Social-Media-Strategy-in-a-Noisy-Online-World/101309737?via=browse-popular-business-layout-grid" target="_blank"><span style="font-weight: 400;">Context is Key: Social Media Strategy in a Noisy Online World</span></a><span style="font-weight: 400;"> with Gary Vaynerchuk, and many more.</span></p>\n<p><span style="font-weight: 400;">To be eligible, you must currently be a small business owner or employee, and you must be a first-time premium Skillshare member. Visit </span><a href="http://skl.sh/mailchimp-scholarship" target="_blank"><span style="font-weight: 400;">the Skillshare scholarships page</span></a><span style="font-weight: 400;"> for more information and to apply.</span></p>\n<p><span style="font-weight: 400;">Oh, and about those new MailChimp courses I mentioned. Our first course took a high-level look at email marketing and everything you need to think about before sending your first newsletter. Building on that, we’ve created 3 new courses that take a deeper look at the elements of a great email marketing plan: writing content, designing campaigns, and reviewing analytics. And best of all, they&#8217;re free.</span></p>\n<figure class=''img''>\n<a href="https://www.skillshare.com/classes/business/Email-Marketing-Essentials-Measuring-Impact-with-Metrics/861339947?utm_campaign=video-embed-861339947&#038;utm_source=Video&#038;utm_medium=video-embed" target="_blank"><br />\n<img src="http://blog.mailchimp.com/wp-content/uploads/2015/09/skillshare-video-poster.png"><br />\n</a><br />\n</figure>\n<p>Here&#8217;s what we&#8217;re offering:</p>\n<ul>\n<li><a href="http://skl.sh/1KCjdkX" target="_blank"><span style="font-weight: 400;">Email Marketing Essentials: Writing Effective Emails</span></a><span style="font-weight: 400;">, with our communications director </span><a href="https://twitter.com/katekiefer" target="_blank"><span style="font-weight: 400;">Kate Kiefer Lee</span></a><span style="font-weight: 400;">, covers best practices for writing online, how to optimize each written element of a marketing email, content ideas for achieving your business goals, and tips for editing before you send.</span></li>\n<li><a href="http://skl.sh/1TCcpmH" target="_blank"><span style="font-weight: 400;">Email Marketing Essentials: Designing Effective Emails</span></a><span style="font-weight: 400;">, with our lead email developer and UX designer </span><a href="https://twitter.com/flcarneiro" target="_blank"><span style="font-weight: 400;">Fabio Carneiro</span></a><span style="font-weight: 400;">, covers the difference between web design and email design, key considerations for designing and developing emails, and best design practices for the 4 most common email types.</span></li>\n<li><a href="http://skl.sh/1iITYBM" target="_blank"><span style="font-weight: 400;">Email Marketing Essentials: Measuring Impact with Metrics,</span></a><span style="font-weight: 400;"> with our chief data scientist </span><span style="font-weight: 400;"><a href="https://twitter.com/John4man" target="_blank">John Foreman</a> (watch a preview of this one above)</span><span style="font-weight: 400;">, dives into basic metrics to consider, key metrics by email type, and ways to test and optimize your campaigns.</span></li>\n</ul>\n<p><span style="font-weight: 400;">I hope these courses—along with the rest of Skillshare&#8217;s catalog—help you continue to build your business. If there are any topics you&#8217;d love to see MailChimp cover in a future course, let us know. We&#8217;re not done yet!</span></p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:29:"http://search.yahoo.com/mrss/";a:1:{s:7:"content";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:109:"http://blog.mailchimp.com/wp-content/uploads/2015/09/skill_share_ads_facebookcard_1200x628_retina-600x314.jpg";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:109:"http://blog.mailchimp.com/mailchimp-and-skillshare-empower-small-businesses-with-100000-in-scholarships/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"2";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:7;a:6:{s:4:"data";s:74:"\n    \n    \n    \n    \n    \n    		\n		\n\n    \n    \n        \n        \n    \n    ";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:6:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:46:"Maintaining Company Culture Through Onboarding";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://blog.mailchimp.com/maintaining-company-culture-through-onboarding/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:82:"http://blog.mailchimp.com/maintaining-company-culture-through-onboarding/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 29 Sep 2015 13:46:16 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:2:{i:0;a:5:{s:4:"data";s:14:"MailChimp News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:15:"company culture";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://blog.mailchimp.com/?p=88753";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:312:"In the past year, MailChimp has hired 132 new employees. By the end of 2015, we’ll have more than 500 folks working here. When I started 2 years ago, I was employee number 207 and even that seemed wild to folks who’d been here since the double-digit days. It’s really exciting to be growing this fast, ...";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"Shauna";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:5525:"<p><span style="font-weight: 400;">In the past year, MailChimp has hired 132 new employees. By the end of 2015, we’ll have more than 500 folks working here. When I started 2 years ago, I was employee number 207 and even </span><i><span style="font-weight: 400;">that</span></i><span style="font-weight: 400;"> seemed wild to folks who’d been here since the double-digit days. It’s really exciting to be growing this fast, but it’s also scary. What does it mean for our company culture? Are we turning into a big, soulless corporation? Wait a second, where did all these </span><a href="http://media.thedailytouch.com/2014/03/uhyeah.gif" target="_blank"><span style="font-weight: 400;">TPS reports</span></a><span style="font-weight: 400;"> come from?!</span></p>\n<p><span style="font-weight: 400;">We’ve done a lot of work to make sure we stay weird as we grow, like </span><a href="http://blog.mailchimp.com/how-mailchimp-university-helps-us-stay-weird-and-grow-strong/" target="_blank"><span style="font-weight: 400;">developing MailChimp University</span></a><span style="font-weight: 400;"> and designating Marti Wolf as </span><a href="http://rocketship.fm/episodes/ep-157-marti-wolf/" target="_blank"><span style="font-weight: 400;">our Chief Culture Officer</span></a><span style="font-weight: 400;">. We’ve also made changes that strengthen—not just maintain—our company culture. And that’s how I became MailChimp’s first-ever new hire ambassador.</span></p>\n<p><span style="font-weight: 400;">It used to be that new MailChimp employees were greeted at the front door, handed some HR paperwork to sign, issued a security badge, and dropped off at their desks. Maybe someone would take them to lunch at the Thai place around the corner. Otherwise, that was pretty much it.</span></p>\n<p><span style="font-weight: 400;">Eventually we realized it was taking new folks months and months to get comfortable at MailChimp, and no wonder! That was unacceptable. <a href="https://kateheddleston.com/blog/onboarding-and-the-cost-of-team-debt" target="_blank">Onboarding is important</a>, and can make our company more efficient and fun when it&#8217;s done right. So earlier this year I was given the opportunity to improve and manage the onboarding of all our new hires, from their first day through 6 months.</span></p>\n<p><span style="font-weight: 400;">Now, I send every new hire a detailed email of what their first day will be like so they know exactly what to expect. That kicks off an </span><a href="http://mailchimp.com/features/automation/" target="_blank"><span style="font-weight: 400;">automation</span></a><span style="font-weight: 400;"> series that delivers important information about schedules, benefits, and more to their inboxes throughout the first few weeks. I also work with managers to ensure that new hires understand team-specific skills and processes.</span></p>\n<p><span style="font-weight: 400;">When new hires arrive at the office on day 1, they each get a tote bag full of MailChimp swag and a full tour of the office so they can learn all the most important stuff, like where we keep all the snacks (duh). Then we have our first of many get-to-know-you lunches with folks from around the company. No more feeling like the awkward new kid on the first day of school.</span></p>\n<p><span style="font-weight: 400;">That first week we also host departmental overviews to show what each team does and how they fit into the big picture of MailChimp. New hires spend the afternoons with their own teams and managers, easing into their new roles and responsibilities.</span></p>\n<p><span style="font-weight: 400;">All this new information can be overwhelming, so I throw in some fun stuff, too. We don’t do </span><a href="http://stream1.gifsoup.com/view7/20140428/5025956/mean-girls-trust-falls-o.gif" target="_blank"><span style="font-weight: 400;">trust falls</span></a><span style="font-weight: 400;"> or make friendship bracelets, but we’ve developed a pretty intense scavenger hunt that sends new hires on wild missions all over the office. Every time I’m sent a selfie with an alien butt (don’t ask), I love my job even more.</span></p>\n<figure class="img column-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/09/the-chimpanion-program-672x504.png" alt="the-chimpanion-program" /></figure>\n<p><span style="font-weight: 400;">Week 2 eases up a bit. That’s when the support team’s new folks begin their month of super in-depth app training, and all the other newbies get a full-day boot camp. It’s also when our Chimpanions step in.</span></p>\n<p><span style="font-weight: 400;">This is another new thing we’re doing, where we pair a new hire with a more seasoned MailChimp employee in a different department. They&#8217;re not always a perfect match, and that&#8217;s OK. We’ve had pairs realize they’re basically the same person, and others that had absolutely nothing in common but still got along well. </span></p>\n<p><span style="font-weight: 400;">After each new hire is settled in, we gather feedback from them and adjust the next round of onboarding accordingly. It’s a perpetual work in progress, and it’s so gratifying. Week after week, I get to help our new folks learn the ropes, then watch them flourish as they find their niches. But I can’t stand around admiring them for too long—there’s always a new group <a href="http://mailchimp.com/about/jobs/" target="_blank">coming around the corner</a>.</span></p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:29:"http://search.yahoo.com/mrss/";a:1:{s:7:"content";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:87:"http://blog.mailchimp.com/wp-content/uploads/2015/09/the-chimpanion-program-600x450.png";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:78:"http://blog.mailchimp.com/maintaining-company-culture-through-onboarding/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"4";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:8;a:6:{s:4:"data";s:80:"\n    \n    \n    \n    \n    \n    		\n		\n		\n		\n\n    \n    \n        \n        \n    \n    ";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:6:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:46:"Expanding on Our Marketing Automation Features";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://blog.mailchimp.com/expanding-on-our-marketing-automation-features/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:82:"http://blog.mailchimp.com/expanding-on-our-marketing-automation-features/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Thu, 24 Sep 2015 16:30:54 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:4:{i:0;a:5:{s:4:"data";s:14:"Autoresponders";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:14:"MailChimp News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:10:"automation";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:7:"release";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://blog.mailchimp.com/?p=89145";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:378:"We&#8217;ve spent the last week rolling out MailChimp Pro, our powerful new feature set for quickly growing businesses with lists of 50,000 or more. But we also made some important updates to our automation features in the latest release. More than 140,000 of our customers now use MailChimp&#8217;s marketing automation features, and since we announced our new workflows ...";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:5:"Chris";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:4983:"<p>We&#8217;ve spent the last week <a href="https://blog.mailchimp.com/tag/mailchimp-pro/" target="_blank">rolling out MailChimp Pro</a>, our powerful new feature set for quickly growing businesses with lists of 50,000 or more. But we also made some important updates to <a href="http://mailchimp.com/features/automation/" target="_blank">our automation features</a> in the latest release.</p>\n<p>More than <a href="https://twitter.com/benchestnut/status/627181400777748481" target="_blank">140,000 of our customers</a> now use MailChimp&#8217;s marketing automation features, and since we announced our <a href="https://blog.mailchimp.com/new-automation-workflows-and-api-functionality/">new workflows and API functionality</a>, we&#8217;ve sent an additional 350 million automated messages.</p>\n<p>That kind of usage and growth generates a lot of feedback, and many of the most popular requests from users have been for workflow queue management, individual email segmentation, and sending time configuration. Well, you asked, and we&#8217;ve delivered. All 3 of those automation features are now available. Let&#8217;s take a look at each.</p>\n<h2>Workflow queue management</h2>\n<p>Workflow queue management gives you better control over the pace of your automated emails. Think of it as a way to experiment with your automation ideas on the people who will use them the most.</p>\n<figure class="img column-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/09/Screen-Shot-2015-09-03-at-10.09.22-PM-672x118.png" alt="Add to Workflow" /></figure>\n<p>Here&#8217;s a fun test: once your workflow is designed and started, add 100 of your most engaged subscribers, and watch their performance. When you see how a small segment of engaged customers reacts to an email, you can make any necessary final adjustments to perfect your workflow recipe. After that, add the remaining subscribers and watch your engagement improve!</p>\n<figure class="img column-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/09/Screen-Shot-2015-09-03-at-10.10.22-PM-672x462.png" alt="Add to Workflow" /></figure>\n<p>We&#8217;ve also added 2 new merge tags (*|AUTOMATION:WORKFLOWREMOVEURL|* and *|AUTOMATION:WORKFLOWADDURL|*) which allow subscribers to add and remove themselves from workflows. For instance, try creating a standard email campaign promoting your education series workflows and allow subscribers to control which emails they receive. Previously, if a user wanted to opt out of an automation series, they&#8217;d have to unsubscribe from your list, but that&#8217;s no longer the case. Everybody wins.</p>\n<h2>Individual email segmentation and sending times</h2>\n<p>Now that individual emails can contain segmentation conditions, e-commerce customers can stop sending marketing materials to subscribers who have purchased their product mid-workflow. This feature enables some of the most powerful automation scenarios in MailChimp to date.</p>\n<figure class="img column-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/09/Screen-Shot-2015-09-03-at-10.27.40-PM-672x111.png" alt="Individual Email" /></figure>\n<p>There are so many scenarios where this will be useful. Subscribers who finished an online educational course can stop receiving the email series and move on to the next program by using our <a href="https://blog.mailchimp.com/send-targeted-emails-based-on-website-activity/" target="_blank">website tracking integration, Goal</a>. E-commerce customers using <a href="http://kb.mailchimp.com/integrations/other-integrations/about-ecommerce360" target="_blank">eCommerce360</a> can stop sending to subscribers who purchased from their store. We&#8217;re excited to see folks putting this to use, not to mention the creative ways they&#8217;ll find to <a href="http://blog.mailchimp.com/how-wilco-used-mailchimp-automation-to-release-its-new-album/" target="_blank">delight their subscribers</a>.</p>\n<h2>Loose ends and looking forward</h2>\n<p>There&#8217;s one more powerful automation update, but <a href="http://blog.mailchimp.com/wp-content/uploads/2015/09/IMG_3618-e1441334858914.jpeg" target="_blank">listen closely</a>, because this one can be a little tricky if used improperly. We&#8217;ve enabled triggering automation emails on subscribers who didn&#8217;t open or didn&#8217;t click on the previous email in a series. This is a pretty valuable automation feature, but you&#8217;ll want to be careful not to annoy your subscribers, which can lead to abuse reports.</p>\n<figure class="img half-width"><img class=" aligncenter" src="http://blog.mailchimp.com/wp-content/uploads/2015/09/Screen-Shot-2015-09-03-at-10.37.28-PM-282x233.png" alt="Not Opened" /></figure>\n<p>We&#8217;re always listening hard and changing fast, so you can look forward to more automation updates in the future. In the meantime, dig into this new functionality! We can&#8217;t wait to see what you do with it.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:29:"http://search.yahoo.com/mrss/";a:1:{s:7:"content";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:102:"http://blog.mailchimp.com/wp-content/uploads/2015/09/Screen-Shot-2015-09-03-at-10.10.22-PM-600x412.png";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:78:"http://blog.mailchimp.com/expanding-on-our-marketing-automation-features/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:2:"13";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:9;a:6:{s:4:"data";s:83:"\n    \n    \n    \n    \n    \n    		\n		\n		\n		\n		\n\n    \n    \n        \n        \n    \n    ";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:6:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:37:"Compliance Insights for MailChimp Pro";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:64:"http://blog.mailchimp.com/compliance-insights-for-mailchimp-pro/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:73:"http://blog.mailchimp.com/compliance-insights-for-mailchimp-pro/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 22 Sep 2015 17:50:57 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:5:{i:0;a:5:{s:4:"data";s:14:"deliverability";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:14:"MailChimp News";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:2;a:5:{s:4:"data";s:11:"Spam Topics";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:3;a:5:{s:4:"data";s:19:"compliance insights";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:4;a:5:{s:4:"data";s:13:"MailChimp Pro";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:34:"http://blog.mailchimp.com/?p=86257";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:346:"Like all the other departments at MailChimp, the primary goal of the Compliance team is to empower users. We’re always looking for ways to do this better, and working on MailChimp Pro, our brand new feature set for quickly growing businesses with lists of 50,000 or more, offered tons of opportunities. So while Pro was incubating, a few ...";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:7:"Matthew";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:8687:"<p>Like all the other departments at MailChimp, the primary goal of the Compliance team is to empower users. We’re always looking for ways to do this better, and <a href="https://blog.mailchimp.com/tag/mailchimp-pro/" target="_blank">working on MailChimp Pro</a>, our brand new feature set for quickly growing businesses with lists of 50,000 or more, offered tons of opportunities. So while Pro was incubating, a few of us got together to figure out how we could provide users with better insights into our process.</p>\n<p>Some folks might think Compliance exists only to find bad users and shut them down, but there’s more to it than that. We know good senders occasionally mess up, and we also know that getting shut down for a single mistake can materially impact a user’s business. A lot of folks worry, “Am I OK, or am I doing something wrong?”</p>\n<p>We built Compliance Insights to help MailChimp Pro users answer that question for themselves. We believe our customers should have the ability to clearly understand their compliance risks and the data they need to avoid abuse incidents whenever possible.</p>\n<p>To make <a href="http://kb.mailchimp.com/accounts/compliance-insights/about-compliance-insights" target="_blank">Compliance Insights</a>, we knew we’d have to expose some of the inner workings of our compliance process. Namely, that of <a href="http://mailchimp.com/omnivore/">Omnivore</a>, the automated part of our abuse-prevention initiative that scans data across our entire system and flags accounts for the folks on our Compliance team to review. Here’s how we did it.</p>\n<h2>Tracking your current reputation</h2>\n<p>When you&#8217;re sending email, reputation is everything. You have a reputation with your subscribers, your From address has a reputation, the IPs you send through have reputations, and every ISP you send to decides for themselves what these reputations are. Even Omnivore has a concept of what each user’s reputation is with MailChimp.</p>\n<p>Before, only our Compliance team had access to Omnivore&#8217;s reputation info. But now, MailChimp Pro users can easily see this reputation for themselves:</p>\n<figure class="img full-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/09/chart_currentreputation-1008x149.png" alt="chart_currentreputation" /></figure>\n<p>The new visual scale is built from two key elements in Omnivore’s data stream. First, Omnivore tracks your entire engagement history for every campaign you’ve ever sent. That’s the history of your opens, clicks, bounces, and abuse complaints. We weigh new campaigns more heavily than older campaigns to ensure we know how users are changing over time. Second, Omnivore tracks campaigns with particularly high bounce or abuse rates. We call a campaign like this a "negative send event," but because that&#8217;s an ominous and terrifying phrase, let&#8217;s use <img src="http://s.w.org/images/core/emoji/72x72/1f4ec.png" alt="&#x1f4ec;" class="wp-smiley" style="height: 1em; max-height: 1em;" /><img src="http://s.w.org/images/core/emoji/72x72/1f4a9.png" alt="&#x1f4a9;" class="wp-smiley" style="height: 1em; max-height: 1em;" /> for the purposes of this blog post.<br />\n<a href="http://blog.mailchimp.com/wp-content/uploads/2015/09/chart_currentreputation.png"></a></p>\n<p>The math that combines our 2 data streams into a single value is fairly complex, but here&#8217;s the simplified version: your engagement history determines how high you can go, and every <img src="http://s.w.org/images/core/emoji/72x72/1f4ec.png" alt="&#x1f4ec;" class="wp-smiley" style="height: 1em; max-height: 1em;" /><img src="http://s.w.org/images/core/emoji/72x72/1f4a9.png" alt="&#x1f4a9;" class="wp-smiley" style="height: 1em; max-height: 1em;" /> drags you down. If you look closely at the reputation scale, you’ll see a gray zone that reflects the reputation most MailChimp users have.</p>\n<p>Since most users don’t have a <img src="http://s.w.org/images/core/emoji/72x72/1f4ec.png" alt="&#x1f4ec;" class="wp-smiley" style="height: 1em; max-height: 1em;" /><img src="http://s.w.org/images/core/emoji/72x72/1f4a9.png" alt="&#x1f4a9;" class="wp-smiley" style="height: 1em; max-height: 1em;" /> in their recent history, you’ll likely only end up below this box if you’re a new user who hasn&#8217;t built up a campaign history yet (no big deal) or if you&#8217;re recovering from a campaign with particularly bad stats (slightly bigger deal). If you end up below the gray box because of the latter, you can use this tool to track how quickly you rebuild your reputation.</p>\n<p>It’s worth noting that sending another <img src="http://s.w.org/images/core/emoji/72x72/1f4ec.png" alt="<img src="http://s.w.org/images/core/emoji/72x72/1f4ec.png" alt="&#x1f4ec;" class="wp-smiley" style="height: 1em; max-height: 1em;" />" class="wp-smiley" style="height: 1em; max-height: 1em;" /><img src="http://s.w.org/images/core/emoji/72x72/1f4a9.png" alt="<img src="http://s.w.org/images/core/emoji/72x72/1f4a9.png" alt="&#x1f4a9;" class="wp-smiley" style="height: 1em; max-height: 1em;" />" class="wp-smiley" style="height: 1em; max-height: 1em;" /> while you’re below the gray box will lower your reputation even further. This can impact the severity of the response from Omnivore and our Compliance team. After each <img src="http://s.w.org/images/core/emoji/72x72/1f4ec.png" alt="<img src="http://s.w.org/images/core/emoji/72x72/1f4ec.png" alt="&#x1f4ec;" class="wp-smiley" style="height: 1em; max-height: 1em;" />" class="wp-smiley" style="height: 1em; max-height: 1em;" /><img src="http://s.w.org/images/core/emoji/72x72/1f4a9.png" alt="<img src="http://s.w.org/images/core/emoji/72x72/1f4a9.png" alt="&#x1f4a9;" class="wp-smiley" style="height: 1em; max-height: 1em;" />" class="wp-smiley" style="height: 1em; max-height: 1em;" />, you’ll have to take specific actions in order to start rebuilding your reputation. These actions are designed to set you on the right path to building a healthy and engaged list.</p>\n<h2>Improving your future reputation</h2>\n<p>When building Compliance Insights, we wanted to do more than simply show users where they stand. With the entire engagement history of MailChimp’s 9 million users at its disposal, we realized Omnivore should be able to offer specific and actionable data about what items users can focus on to proactively prevent abuse issues and increase engagement.</p>\n<p>Underneath the reputation scale, you’ll see status boxes for the 4 key engagement factors Omnivore tracks: opens, clicks, bounces, and abuse complaints. Your performance on these 4 factors directly affects how high your reputation can go. They also determine how quickly you rebuild reputation after a <img src="http://s.w.org/images/core/emoji/72x72/1f4ec.png" alt="&#x1f4ec;" class="wp-smiley" style="height: 1em; max-height: 1em;" /><img src="http://s.w.org/images/core/emoji/72x72/1f4a9.png" alt="&#x1f4a9;" class="wp-smiley" style="height: 1em; max-height: 1em;" />.</p>\n<figure class="img full-width"><img src="http://blog.mailchimp.com/wp-content/uploads/2015/09/chart_historicalreputation-1008x175.png" alt="chart_historicalreputation" /></figure>\n<p>Under each factor, we’ve included a link to a <a href="http://kb.mailchimp.com/">MailChimp Knowledge Base</a> article that focuses on specific actions you can take to improve your campaign performance. We’ve found that users who aren’t following the best-practice recommendations in our KB articles tend to have a higher risk of sending campaigns with critically high bounce and/or abuse rates. If your historical bounce or abuse rates are listed as “poor,” Omnivore is trying to tell you that you have an increased risk of committing a <img src="http://s.w.org/images/core/emoji/72x72/1f4ec.png" alt="&#x1f4ec;" class="wp-smiley" style="height: 1em; max-height: 1em;" /><img src="http://s.w.org/images/core/emoji/72x72/1f4a9.png" alt="&#x1f4a9;" class="wp-smiley" style="height: 1em; max-height: 1em;" />. By improving these factors, you can prevent a worse result down the road.</p>\n<h2>Success through empowered sending</h2>\n<p>Our compliance process isn&#8217;t always a fun topic, but it&#8217;s much better to learn about ahead of time than it is to play catch-up after Omnivore has flagged your account. Hopefully, tracking your MailChimp reputation and understanding the reasons behind it will set you up for success. If mistakes do happen, the data you&#8217;ll see in Compliance Insights is the same data our human review team sees behind the scenes. With great power comes great responsibility, so use this information wisely!</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:29:"http://search.yahoo.com/mrss/";a:1:{s:7:"content";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:84:"http://blog.mailchimp.com/wp-content/uploads/2015/09/compliance-insights-600x462.jpg";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:69:"http://blog.mailchimp.com/compliance-insights-for-mailchimp-pro/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"0";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}s:27:"http://www.w3.org/2005/Atom";a:1:{s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:3:{s:4:"href";s:31:"http://blog.mailchimp.com/feed/";s:3:"rel";s:4:"self";s:4:"type";s:19:"application/rss+xml";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:44:"http://purl.org/rss/1.0/modules/syndication/";a:2:{s:12:"updatePeriod";a:1:{i:0;a:5:{s:4:"data";s:6:"hourly";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:15:"updateFrequency";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}}}}}}s:4:"type";i:128;s:7:"headers";a:13:{s:6:"server";s:6:"Apache";s:12:"x-powered-by";s:22:"W3 Total Cache/0.9.4.1";s:7:"expires";s:29:"Mon, 16 Nov 2015 02:27:27 GMT";s:13:"cache-control";s:41:"public, must-revalidate, proxy-revalidate";s:6:"pragma";s:6:"public";s:10:"x-pingback";s:36:"http://blog.mailchimp.com/xmlrpc.php";s:13:"last-modified";s:29:"Tue, 10 Nov 2015 17:26:21 GMT";s:15:"x-frame-options";s:10:"SAMEORIGIN";s:12:"content-